one ********** */\n@media (max-width: 400px) {\n.grey-well {\n  padding: 1rem;\n  margin: auto 0 1.5rem 0;\n  background-color: #f5f5f5;\n  border: 1px solid #e3e3e3;\n  border-radius: 0px;\n  -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,0.05);\n  box-shadow: inset 0 1px 1px rgba(0,0,0,0.05);\n} \n}\n/* -- end added by Tulin -- */\n/* -- begin added by Gedi --*/\n.back-button {\n    margin-top: 1em;\n}\n.card-body a {\n    border-bottom: dotted 0.1rem #333333;\n}\n.back-to-toplink {\n    text-align: right;\n    padding-top: 5px;\n    font-size: 1.2em;\n}\n.back-to-toplink a {\n    border-bottom: dotted 0.1rem #333333;\n}\n.flag-container {\n    float: right !important;\n}\n.country {\n    box-shadow: 5px 5px 3px #f2f2f2;\n    padding: 10px;\n    margin-bottom: 10px;\n}\n#memberStatesIndex {\n    border: solid 1px #000;\n    background: #eee;\n    padding: 10px;\n    text-align: center;\n    margin-bottom: 1em;\n}\n#memberStatesIndex a {\n    padding: 9px;\n    font-size: 1.25em;\n    font-weight: 700;\n}\n.member-state-grouping-letter {\n    text-align: center;\n    font-size: 2em !important;\n    font-weight: bold;\n    margin: 1em 0 0.5em 0;\n}\n.member-state-grouping-letter a {\n    border: none;\n}\n.onimage_title {\n    display: none;\n}\n.main-container.container {\n    margin-top: 2rem;\n    margin-bottom: 3em;\n}\n.image-metadata {\n    border:none;\n    margin-bottom: 0px;\n    padding-bottom: 0px;\n}\n.carousel-caption.top-text { \n    bottom: 30px;\n    padding: 10px;\n    background: rgba(77,77,77,0.9);\n    border-radius: 10px;\n    left: 5%;\n    right: 5%;\n}\n.carousel-caption.top-text p {\n    text-align: left;\n    color:#ffffff;\n}\n.carousel-caption.top-text button {\n  background-color: #009edb;\n}\n.section1 {\n  margin-top: 15px!important;\n    margin-bottom: 25px !important;\n}\nh2.international-selection {\n  text-align: center;\n}\n.observances {\n  background: #009EDB;\n  padding: 15px 0;\n}\n.observance-item {\n  background: #4d4d4d;\n}