ce=null, ga_send = 0) {\r\n        var quantity = $(\'#quantity\').val();\r\n        var customer_token = $(\'#customer_token\').val();\r\n        var rid = get_url_parameter(\'rid\');\r\n        var click_id = get_url_parameter(\'Click_ID\');\r\n      \r\n        //var present_id = $(\'[name=\"present_id\"]:checked\').val();\r\n\r\n        var present_id_list = $(\'input[name=\"present_id\"]:checked\');\r\n        var present_id = \"\";\r\n        present_id_list.each(function () { \r\n            if (present_id.length === 0) {\r\n                present_id = present_id + this.value;        \r\n            }  else {\r\n                present_id = present_id + \"-\"  + this.value ;        \r\n            }\r\n            \r\n        });\r\n\r\n        if(source == null){\r\n            var source = \'\';\r\n        }\r\n        var sourceurl;\r\n        if(source != null && source != \"\"){\r\n            sourceurl = \'?source=\' + source;\r\n        }\r\n        $.ajax({\r\n            url  : \'/api/shopping/addSubscription\',\r\n            type : \'post\',\r\n            data : { \'subscription_id\' : subscription_id , \'quantity\' : quantity , \'situation\' : \'single\' , \'present_id\' : present_id , \'rid\' : rid, \'click_id\' : click_id , \'source\' : source} ,\r\n            dataType : \'json\',\r\n            async : false ,\r\n            error : function() {\r\n                console.log(\'&#31995;&#32113;&#24537;&#30860;&#20013;!&#35531;&#31245;&#24460;&#20877;&#35430;\');\r\n            } ,\r\n            success : function ( response ){\r\n                if ($(\'.msg-popup\').length && $(\'.popup-text\').length) {\r\n                     // &#22914;&#26524;&#26377;popup&#30340;&#20195;&#30908;&#25165;&#22519;&#34892;\r\n                    if(response[\'message\']!=\"&#30906;&#35469;&#35330;&#21934;&#20839;&#23481;\"){\r\n                        $(\'.msg-popup\').addClass(\'active\');\r\n                        $(\'.popup-text\').text(response[\'message\']);\r\n                    }\r\n                } else {\r\n                    // &#22914;&#26524;&#27794;&#26377;popup&#23601;&#29992;alert\r\n                    alert(response[\'message\']);\r\n                }\r\n                get_customer_order_total();\r\n                if ( response[\'status\'] == \'1\' ) {\r\n                    if(ga_send == 1){\r\n                            // var product_name = $(\"a:focus\").attr(\'data-product-name\');\r\n                            // var product_price = parseInt($(\"a:focus\").attr(\'data-product-price\'));\r\n                            var product_name = $(\"#buy_\"+subscription_id+\"\").data(\'product-name\');\r\n                            var product_price = $(\"#buy_\"+subscription_id+\"\").data(\'product-price\');\r\n                            product_price = product_price.toString();\r\n                            product_price = parseInt(product_price.replace(/,/g,\'\').replace(/ /g,\'\').replace(/NT/g,\'\').replace(/\\$/g,\'\'));\r\n                            // alert(product_name);\r\n                            // alert(product_price);\r\n                            dataLayer.push({ ecommerce: null });\r\n                            dataLayer.push({\r\n                                \'event\': \'add_to_cart\', \r\n                                \'ecommerce\': {\r\n                                    \'currency\': \'TWD\',                             \r\n                                    \'items\': [{                         \r\n                                        \'item_name\': product_name, \r\n                                        \'price\': product_price,\r\n                                        \'currency\': \'TWD\'\r\n                                    }]\r\n                                }   \r\n                            });\r\n                            //TenMax&#36861;&#36452;&#30908;start\r\n                            var product_id = $(\"#buy_\"+subscription_id+\"\").data(\'product-id\');\r\n                            var product_category = $(\"#buy_\"+subscription_id+\"\").data(\'product-category\');\r\n                            // alert(product_id);\r\n                            // alert(product_category);\r\n                            var __wbq_var = {\r\n                                \"content\": {\r\n                                    \"couponName\": \"\",\r\n                                    \"productID\": product_id,\r\n                                    \"price\": product_price,\r\n                                    \"productName\": product_name,\r\n                                    \"productCategory\": product_category\r\n                                }\r\n                            };\r\n                            wbq(\'track\', \'AddToCart\', __wbq_var);\r\n                            //TenMax&#36861;&#36452;&#30908;end\r\n                    }\r\n                    //return false;\r\n                    window.location.href = \'/checkout\' ;\r\n                }\r\n            }\r\n        });\r\n   }\r\n\r\n   function add_subscription_staging ( subscription_id ) {\r\n        var rid = get_url_parameter(\'rid\');\r\n        $.ajax({\r\n            url: \'/api/staging/addSubscription\',\r\n            type: \'post\',\r\n            data: { \'subscription_id\' : subscription_id , \'quantity\' : 1 , \'situation\' : \'single\' , \'rid\' : rid } ,\r\n            dataType: \'json\',\r\n            async : false ,\r\n            error : function() {\r\n                console.log(\'&#31995;&#32113;&#24537;&#30860;&#20013;!&#35531;&#31245;&#24460;&#20877;&#35430;\');\r\n            } ,\r\n            success : function ( response ){\r\n                alert( response[\'message\'] );\r\n                get_customer_order_total();\r\n                if ( response[\'status\'] == \'1\' ) {\r\n                    window.location.href = \'/checkout_staging\';\r\n                }\r\n            }\r\n        