/addLecture\',\r\n            type: \'post\',\r\n            data: { \'lecture_id\' : lecture_id , \'quantity\' : 1 } ,\r\n            dataType: \'json\',\r\n            async : false ,\r\n            error : function() {\r\n                console.log(\'&#31995;&#32113;&#24537;&#30860;&#20013;!&#35531;&#31245;&#24460;&#20877;&#35430;\');\r\n            } ,\r\n            success : function ( response ){\r\n\r\n                alert( response[\'message\'] );\r\n                get_customer_order_total();\r\n                if ( response[\'status\'] == \'1\' ) {\r\n                    window.location.href = \'/checkout\';\r\n                }\r\n                \r\n            }\r\n        });\r\n   }    \r\n\r\n</script>        <!-- &#21152;&#20837;&#36861;&#36452;&#29992; JS  -->\r\n<script type=\"text/javascript\">\r\n    function track_book ( book_id ,ga_send = 0) {\r\n        $.ajax({\r\n            url: \'/api/book/add\',\r\n            type: \'post\',\r\n            data: {  \'book_id\' : book_id  } ,\r\n            dataType: \'json\',\r\n            async : false ,\r\n            error : function () {\r\n                console.log(\'&#31995;&#32113;&#24537;&#30860;&#20013;!&#35531;&#31245;&#24460;&#20877;&#35430;\');\r\n            } ,\r\n            success : function ( response ){\r\n                if(ga_send == 1 && response[\'message\'] == \'&#21152;&#20837;&#25910;&#34255;&#25104;&#21151;&#65281;\'){\r\n                    // var product_name = $(\"a:focus\").attr(\'data-product-name\');\r\n                    // var product_price = parseInt($(\"a:focus\").attr(\'data-product-price\').replace(/,/g,\'\').replace(/ /g,\'\').replace(/NT/g,\'\').replace(/\\$/g,\'\'));\r\n                    var product_name = $(\"#track_\"+book_id+\"\").data(\'product-name\');\r\n                    var product_price = $(\"#track_\"+book_id+\"\").data(\'product-price\');\r\n                    product_price = product_price.toString();\r\n                    product_price = parseInt(product_price.replace(/,/g,\'\').replace(/ /g,\'\').replace(/NT/g,\'\').replace(/\\$/g,\'\')); \r\n                    // alert(product_name);\r\n                    // alert(product_price);\r\n\r\n                    dataLayer.push({ ecommerce: null });\r\n                    dataLayer.push({\r\n                        \'event\': \'add_to_wishlist\', \r\n                        \'ecommerce\': {\r\n                            \'currency\': \'TWD\',                             \r\n                            \'items\': [{                         \r\n                                \'item_name\': product_name, \r\n                                \'price\': product_price,\r\n                                \'currency\': \'TWD\'\r\n                            }]\r\n                        }   \r\n                    });\r\n                    //TenMax&#36861;&#36452;&#30908;start\r\n                    var product_id = $(\"#track_\"+book_id+\"\").data(\'product-id\');\r\n                    var product_category = $(\"#track_\"+book_id+\"\").data(\'product-category\');\r\n                    var __wbq_var = {\r\n                            \"content\": {\r\n                                \"couponName\": \"\",\r\n                                \"productID\": product_id,\r\n                                \"price\": product_price,\r\n                                \"productName\": product_name,\r\n                                \"productCategory\": product_category\r\n                            }\r\n                    };\r\n                    wbq(\'track\', \'AddToWishlist\', __wbq_var);\r\n                    //TenMax&#36861;&#36452;&#30908;end\r\n                }\r\n                alert( response[\'message\'] );\r\n            }\r\n        });\r\n    }\r\n    function track_subscription ( subscription_id , ga_send = 0) {\r\n        $.ajax({\r\n            url: \'/api/subscription/add\',\r\n            type: \'post\',\r\n            data: {  \'subscription_id\' : subscription_id  } ,\r\n            dataType: \'json\',\r\n            async : false ,\r\n            error : function () {\r\n                console.log(\'&#31995;&#32113;&#24537;&#30860;&#20013;!&#35531;&#31245;&#24460;&#20877;&#35430;\');\r\n            } ,\r\n            success : function ( response ){\r\n                if(ga_send == 1 && response[\'message\'] == \'&#21152;&#20837;&#25910;&#34255;&#25104;&#21151;&#65281;\'){\r\n                    //var product_name = $(\"a:focus\").attr(\'data-product-name\');\r\n                    //var product_price = parseInt($(\"a:focus\").attr(\'data-product-price\'));\r\n                    var product_name = $(\"#track_\"+subscription_id+\"\").data(\'product-name\');\r\n                    var product_price = $(\"#track_\"+subscription_id+\"\").data(\'product-price\');\r\n                    product_price = product_price.toString();\r\n                    product_price = parseInt(product_price.replace(/,/g,\'\').replace(/ /g,\'\').replace(/NT/g,\'\').replace(/\\$/g,\'\')); \r\n                    // alert(product_name);\r\n                    // alert(product_price);\r\n                    dataLayer.push({ ecommerce: null });\r\n                    dataLayer.push({\r\n                        \'event\': \'add_to_wishlist\', \r\n                        \'ecommerce\': {\r\n                            \'currency\': \'TWD\',                             \r\n                            \'items\': [{                         \r\n                                \'item_name\': product_name, \r\n                                \'price\': product_price,\r\n                                \'currency\': \'TWD\'\r\n                            }]\r\n                        }   \r\n                    });\r\n                    //TenMax&#36861;&#36452;&#30908;start\r\n                    var product_id = $(\"#track_\"+subscription_id+\"\").data(\'product-id\');\r\n                    var product_category = $(\"#track_\"+subscription_id+\"\").data(\'product-category\');\r\n                    var __wbq_var = {\r\n                            \"content\": {\r\n                                \"couponName\": \"\",\r\n                                \"productID\": product_id,\r\n                                \"price\": product_price,\r\n                                \"productName\": product_name,\r\n                                \"productCategory\": product_category\r\n                            }\r\n                    };\r\n                    wbq(\'track\', \'AddToWishlist\', __wbq_var);\r\n                    //TenMax&#36861;&#36452;&#30908;end\r\n                }\r\n                if ($(\'.msg-popup\').length && $(\'.popup-text\').length) {\r\n                     // &#22914;&#26524;&#26377;popup&#30340;&#20195;&#30908;&#25165;&#22519;&#34892;\r\n                        $(\'.msg-popup\').addClass(\'active\');\r\n                        $(\'.popup-text\').text(response[\'message\']);\r\n                } else {\r\n                    // &#22914;&#26524;&#27794;&#26377;popup&#23601;&#29992;alert\r\n                    alert(response[\'message\']);\r\n                }\r\n            }\r\n        });\r\n    }    \r\n</script>        </body>\n\n        </html>\n', '2025-05-28 16:06:03', '重點1：文章標題與主辦單位\r\n  「與海一體共融，才能海洋永續！」海委會管碧玲攜海大培訓200種子教師：期許造就浪者\r\n  主辦單位：海洋委員會及國立臺灣海洋大學臺灣海洋教育中心\r\n\r\n重點2：活動目的與時間\r\n  目的：推廣海洋素養教育、強化我國教師海洋素養教學能量\r\n  時間：114年5月23日（星期五）至25日（星期日）\r\n\r\n重點3：活動內容與參與者\r\n  以強調實作與探究精神的海洋科學教材（Ocean Science Sequence, OSS）為基礎\r\n  培訓來自全臺各縣市共200名種子教師\r\n  協助深化對海洋素養之認識與教學實踐能力\r\n\r\n重點4：海洋委員會主委管碧玲致詞重點\r\n  強調海洋與人類共生關係：「只有培養臺灣的下一代知道怎麼跟海洋相處，才能達到海洋永續的目標」\r\n  指出人類呼吸的氧氣有50%至80%來自海洋，海洋提供優質蛋白質滋養生命\r\n\r\n重點5：國立臺灣海洋大學校長許泰文發言\r\n  表示臺灣是海洋國家，提升海洋素養對每個人非常重要\r\n  氣候變遷與海洋環境問題深刻影響人類未來\r\n\r\n重點6：海洋科學教材特色\r\n  採用啟發式教學，讓學生透過實驗、操作、探索及驗證過程認識海洋\r\n  培養對海洋的尊重、感恩態度與價值觀\r\n\r\n重點7：《海洋保育法》進展\r\n  已於113年7月12日立法院三讀通過\r\n  預計114年7月1日由行政院公告正式生效\r\n\r\n重點8：活動意義\r\n  期望培養具備海洋素養核心概念與教材應用能力的種子教師\r\n  協助全臺各級學校落實永續的海洋教育發展機制\r\n  全面提升學生對海洋環境的理解與關懷');
INSERT INTO `article` (`ArticleID`, `Title`, `Category`, `ImageURL`, `Description`, `ArticleURL`, `UserID`, `Content`, `created_at`, `teacher_summary`) VALUES
(19, ' 海保署攜手各界保育小燕鷗與綠蠵龜', 'sdg14', 'uploads/6837387927081_綠蠵龜.jpg', '在小琉球為綠蠵龜裝設衛星發報器，積極守護海洋保育類物種，呼籲全民關注海洋生態', 'https://www.oca.gov.tw/ch/home.jsp?id=14&parentpath=0,2&mcustomize=news_view.jsp&dataserno=202505220001', 4, '<!DOCTYPE html>\n<html lang=\"zh-TW\">\r\n<head>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\">\r\n<meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\r\n<meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\r\n\r\n\r\n<meta property=\"og:title\" content=\"新聞快訊-世界生物多樣性日 海保署攜手各界保育小燕鷗與綠蠵龜 - 海洋委員會海洋保育署\">\r\n<meta property=\"og:type\" content=\"website\">\r\n<meta property=\"og:image\" content=\"https://www.oca.gov.tw/ch/images/all/logo.png\">\r\n<meta property=\"og:description\" content=\"\n \n 每年5月22日是聯合國訂定的「世界生物多樣性日」，海洋委員會海洋保育署推出《燕鷗繁殖期守護行動》多語版本宣導海報，並在小琉球產卵海龜裝設發報器，攜手各界以多元行動積極守護保育類物種，希望喚起全\">\r\n\r\n<title>新聞快訊-世界生物多樣性日 海保署攜手各界保育小燕鷗與綠蠵龜 - 海洋委員會海洋保育署全球資訊網\r\n</title>\r\n\r\n<link type=\"text/css\" rel=\"stylesheet\" href=\"css/style.css\">\r\n<link type=\"text/css\" rel=\"stylesheet\" href=\"css/style_rwd.css\">\r\n<link type=\"text/css\" rel=\"stylesheet\" href=\"css/menu.css\">\r\n<link type=\"text/css\" rel=\"stylesheet\" href=\"css/ap.css\">\r\n\r\n<script type=\"text/javascript\" src=\"js/jquery-3.5.1.min.js\"></script>\r\n<!-- Global site tag (gtag.js) - Google Analytics -->\r\n<script async src=\"https://www.googletagmanager.com/gtag/js?id=G-FGH5CZRN18\"></script>\r\n<script>\r\n	window.dataLayer = window.dataLayer || [];\r\n	function gtag() {\r\n		dataLayer.push(arguments);\r\n	}\r\n	gtag(\'js\', new Date());\r\n\r\n	gtag(\'config\', \'G-FGH5CZRN18\');\r\n</script>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<meta name=\"DC.Title\" content=\"新聞快訊\">\r\n<meta name=\"DC.Creator\" content=\"海洋委員會 海洋保育署\">\r\n<meta name=\"DC.Subject\" content=\"世界生物多樣性日 海保署攜手各界保育小燕鷗與綠蠵龜\">\r\n<meta name=\"DC.Description\" content=\"世界生物多樣性日 海保署攜手各界保育小燕鷗與綠蠵龜\">\r\n<meta name=\"DC.Publisher\"